import React, { useEffect, useState } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck, updateCard, readCard } from "../../utils/api";

function Card() {
  const { deckId, cardId } = useParams();
  const { url } = useRouteMatch();
  const [card, setCard] = useState({});
  const [breadCrumbs, setBreadCrumbs] = useState(<p>breadCrumbs</p>);
  const [cardType, setCardType] = useState(<p>card</p>);
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const [form, setForm] = useState(<p>form</p>);
  const history = useHistory();
  const initialFormState = {
    front: "",
    back: "",
    deckId: deckId,
  };
  const [formData, setFormData] = useState({ ...initialFormState });
  const abortController = new AbortController();

  useEffect(() => {
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);
    if (error) {
      history.push(`/error/${error}`);
    }
    //readCard(cardId, abortController.signal).then(setCard).catch(setError);
    //setFormData({
    //  ...formData,
    //  id: card.id,
    //  front: card.front,
    //  back: card.back,
    //});
    console.log(thisDeck);
    //form handling
    const handleChangeEdit = ({ target }) => {
      setFormData({
        ...formData,
        [target.name]: target.value,
      });
    };
    const handleChange = ({ target }) => {
      setFormData({
        ...formData,
        [target.name]: target.value,
      });
    };
    const handleSubmit = (event) => {
      event.preventDefault();
      console.log(formData);
      const abortController = new AbortController();

      createCard(deckId, formData, abortController.signal)
        .then(setFormData({ ...initialFormState }))
        .catch(setError);
    };
    const handleSubmitEdit = (event) => {
      event.preventDefault();
      console.log(formData);
      const abortController = new AbortController();
      if (cardId) {
        updateCard(formData, abortController.signal)
          .then(setFormData({ ...initialFormState }))
          .catch(setError);
      }
    };
    if (error) {
      history.push(`/error/${error}`);
    }
    if (!cardId) {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Add Card
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">{thisDeck.name}: Add Card</h1>
        </div>
      );
      setForm(
        <form onSubmit={handleSubmit} className="column">
          <label className="container-fluid">
            Front
            <br />
            <textarea
              type="text"
              id="front"
              name="front"
              value={formData.front}
              onChange={handleChange}
              className="w-100"
              placeholder="Front side of card"
            />
          </label>
          <br />
          <label className="container-fluid">
            Back
            <br />
            <textarea
              type="text"
              id="back"
              name="back"
              value={formData.back}
              onChange={handleChange}
              className="w-100"
              placeholder="Back side of card"
            />
          </label>
          <br />
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-secondary m-1"
          >
            Done
          </Link>
          <button type="submit" className="btn btn-primary m-1">
            Save
          </button>
        </form>
      );
    } else {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Edit Card {cardId}
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">Edit Card</h1>
        </div>
      );
      setForm(
        <form onSubmit={handleSubmitEdit} className="column">
          <label className="container-fluid">
            Front
            <br />
            <textarea
              type="text"
              id="front"
              name="front"
              value={formData.front}
              onChange={handleChangeEdit}
              className="w-100"
            />
          </label>
          <br />
          <label className="container-fluid">
            Back
            <br />
            <textarea
              type="text"
              id="back"
              name="back"
              value={formData.back}
              onChange={handleChangeEdit}
              className="w-100"
            />
          </label>
          <br />
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-secondary m-1"
          >
            Cancel
          </Link>
          <button type="submit" className="btn btn-primary m-1">
            Submit
          </button>
        </form>
      );
    }
    if (error) {
      history.push(`/error/${error}`);
    }
  }, []);

  return (
    <section className="card">
      <div className="card-header">
        <Link to="/">Home</Link> /
        <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Add Card
      </div>
      {cardType} {form}
    </section>
  );
}
export default Card;
