import React, { useEffect, useState } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import {
  createCard,
  readDeck,
  updateCard,
  readCard,
  listCards,
} from "../../utils/api";

function DeskView() {
  const { deckId } = useParams();
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const history = useHistory();
  //const [listDeckCards, setListDeckCards] = useState([]);
  const [thisDeckCards, setThisDeckCards] = useState([]);
  useEffect(() => {
    const abortController = new AbortController();
    async function fetchReadDeck(deckId) {
      try {
        console.log(deckId);
        const readDeckInfo = await readDeck(deckId, abortController.signal);
        setThisDeck(readDeckInfo);
      } catch (error) {
        setError(error);
        history.push(`/error/${error}`);
      }
    }
    fetchReadDeck(deckId);
  }, []);
  useEffect(() => {
    console.log(thisDeck);
    const abortController = new AbortController();
    async function fetchDeckCards(deckId) {
      try {
        console.log(deckId);
        const readCards = await readDeck(deckId, abortController.signal);
        setThisDeckCards(readCards);
      } catch (error) {
        setError(error);
        history.push(`/error/${error}`);
      }
    }
    fetchDeckCards(deckId);
  }, [thisDeck]);

  console.log(thisDeckCards);
  const listDeckCards = thisDeckCards.map((card) => (
    <div className="row">
      <div className="col">{card.front}</div>
      <div className="col">{card.back}</div>
    </div>
  ));

  return (
    <div>
      <section className="card">
        <div className="card-header">
          <Link to="/">Home</Link> / {thisDeck.name}
        </div>
        <div className="card-body">
          <div className="card-title">
            <h1>{thisDeck.name}</h1>
          </div>
          <div className="card-text">
            <p>{thisDeck.description}</p>
            <Link
              to={`/decks/${deckId}/edit`}
              type="button"
              className="btn btn-secondary m-1"
            >
              Edit
            </Link>
            <Link
              to={`/decks/${deckId}/study`}
              type="button"
              className="btn btn-primary m-1"
            >
              Study
            </Link>
          </div>
        </div>
      </section>
      {thisDeckCards && (
        <section>
          <h1>Cards</h1>
          {listDeckCards}
        </section>
      )}
    </div>
  );
}

export default DeskView;
