import React, { useEffect, useState, use } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck } from "../../utils/api";

function Card() {
  const { deckId, cardId } = useParams();
  const { url } = useRouteMatch();
  const [card, setCard] = useState({});
  const [breadCrumbs, setBreadCrumbs] = useState(<p>breadCrumbs</p>);
  const [cardType, setCardType] = useState(<p>card</p>);
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const [form, setForm] = useState(<p>form</p>);
  const history = useHistory();
  const initialFormState = {
    front: "Front side of card",
    back: "Back side of card",
    deckId: deckId,
  };
  const [formData, setFormData] = useState({ ...initialFormState });

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);

    if (!cardId) {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Add Card
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">{thisDeck.name}: Add Card</h1>
        </div>
      );
      setForm(
        <form onSubmit={handleSubmit} className="column">
          <label className="container-fluid">
            Front
            <br />
            <textarea
              type="text"
              id="front"
              name="front"
              value={formData.front}
              onChange={handleChange}
              className="w-100"
            />
          </label>
          <br />
          <label className="container-fluid">
            Back
            <br />
            <textarea
              type="text"
              id="back"
              name="back"
              value={formData.back}
              onChange={handleChange}
              className="w-100"
            />
          </label>
          <br />
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-secondary m-1"
          >
            Done
          </Link>
          <button type="submit" className="btn btn-primary m-1">
            Save
          </button>
        </form>
      );
    } else {
      readCard(cardId, abortController.signal).then(setCard).catch(setError);
      if (card) {
        setFormData({
          ...formData,
          id: card.id,
          front: card.front,
          back: card.back,
        });
      }
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Edit Card {cardId}
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">Edit Card</h1>
        </div>
      );
      setForm(
        <form onSubmit={handleSubmit} className="column">
          <label className="container-fluid">
            Front
            <br />
            <textarea
              type="text"
              id="front"
              name="front"
              value={formData.front}
              onChange={handleChange}
              className="w-100"
            />
          </label>
          <br />
          <label className="container-fluid">
            Back
            <br />
            <textarea
              type="text"
              id="back"
              name="back"
              value={formData.back}
              onChange={handleChange}
              className="w-100"
            />
          </label>
          <br />
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-secondary m-1"
          >
            Done
          </Link>
          <button type="submit" className="btn btn-primary m-1">
            Save
          </button>
        </form>
      );
    }
  }, [thisDeck]);

  if (error) {
    history.push(`/error/${error}`);
  }

  //form handling
  const handleChange = ({ target }) => {
    setFormData({
      ...formData,
      [target.name]: target.value,
    });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
    const abortController = new AbortController();
    createCard(deckId, formData, abortController.signal)
      .then(setFormData({ ...initialFormState }))
      .catch(setError);
  };
  if (error) {
    history.push(`/error/${error}`);
  }

  if (!cardId) {
    return (
      <section className="card">
        {breadCrumbs} {cardType} {form}
      </section>
    );
  }
  return (
    <section className="card">
      {breadCrumbs} {cardType} {form}
    </section>
  );
}
export default Card;
