import React from "react";
import deleteCard from "../../utils/api/index";

function ListCard({ card }) {
  const handleDeleteCard = () => {
    const abortController = new AbortController();
    if (
      window.confirm(
        `Do you really want to delete card ${card.id}? There is no going back.`
      )
    ) {
      try {
        deleteCard(card.id, abortController.signal);
        console.log("deleted card");
        window.location.reload();
      } catch (error) {
        console.log(error);
      }
    }
  };
  return (
    <div className="row container">
      <div className="col border border-secondary my-1 rounded">
        {card.front}
      </div>
      <div className="col  border border-secondary my-1 rounded">
        {card.back}
      </div>
      <button
        onClick={handleDeleteCard}
        type="button"
        className="btn btn-danger m-1 ml-auto"
      >
        Delete
      </button>
    </div>
  );
}

export default ListCard;
