import { Link, useParams, useHistory, Switch, Route } from "react-router-dom";
import { readDeck } from "../../utils/api";

function Study() {
  const { deckId } = useParams();
  const [thisDeck, setThisDeck] = useState([]);
  const history = useHistory();
  const [error, setError] = useState(undefined);
  const [cardNumber, setCardNumber] = useState(0);
  const [cardBody, setCardBody] = useState("<h3>Not Enough Cards</h3>");
  const [numberOfCards, setNumberOfCards] = useState(0);
  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);
  }, []);
  if (error) {
    history.push("/error");
  }

  if (thisDeck.length) {
    setNumberOfCards(thisDeck.cards.length);
  }

  if (numberOfCards > 2) {
  }

  return (
    <section className="card">
      <div className="card-header">
        <Link to="/">Home</Link> /
        <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Study
      </div>
      <div className="card-body">
        <h1 className="card-title">Study: {thisDeck.name}</h1>
      </div>
      <div className="card-text">
        <Switch>
          <Route path=""></Route>
          <Route></Route>
        </Switch>
      </div>
    </section>
  );
}

export default Study;
