import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck, readDeck } from "../../utils/api";

function Deck({ deck, cards }) {
  const [thisDeck, setThisDeck] = useState([]);
  const history = useHistory();
  const [error, setError] = useState(undefined);
  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deck.id, abortController.signal).then(setThisDeck).catch(setError);
  }, []);
  if (error) {
    history.push("/error");
  }

  function handleDeleteDeck() {
    if (
      window.confirm(
        `Do you really want to delete deck ${deck.name}? No backsies...`
      )
    ) {
      deleteDeck(deck.id);
      console.log("deleted deck");
      history.push("");
    }
  }
  return (
    <article className="card container-fluid m-1">
      <div className="card-body">
        <h1 className="card-title">{deck.name}</h1>
        <p class="card-text">{deck.description}</p>
        <Link
          to={`/decks/${deck.id}`}
          type="button"
          className="btn btn-dark m-1"
        >
          View
        </Link>
        <Link
          to={`/decks/${deck.id}/study`}
          type="button"
          className="btn btn-dark m-1"
        >
          Study
        </Link>
        <Link
          onClick={handleDeleteDeck}
          type="button"
          className="btn btn-danger m-1"
        >
          Delete
        </Link>
      </div>
      <div class="card-footer">{Object.values(thisDeck).length} Cards</div>
    </article>
  );
}

export default Deck;
