import React, { useState, useEffect } from "react";
import { Link, useParams, useHistory, Switch, Route } from "react-router-dom";
import { readDeck } from "../../utils/api";

function Study() {
  const { deckId } = useParams();
  const [thisDeck, setThisDeck] = useState([]);
  const history = useHistory();
  const [error, setError] = useState(undefined);
  const [cardNumber, setCardNumber] = useState(0);
  const [flip, setFlip] = useState("front");
  const [cardText, setCardText] = useState(<h3>Not Enough Cards</h3>);
  const [numberOfCards, setNumberOfCards] = useState(0);

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);
    if (thisDeck.id) {
      setNumberOfCards(thisDeck.cards.length);
      if (numberOfCards > 2) {
        setCardText(
          <div className="card-body">
            <h1 className="card-title">
              Card {cardNumber + 1} of {numberOfCards}
            </h1>
            <p className="card-text">{thisDeck.cards[cardNumber][flip]}</p>
          </div>
        );
      }
    }
  }, [thisDeck]);

  if (error) {
    history.push("/error", error);
  }

  const handleNext = async () => {
    await setCardNumber(cardNumber + 1);
    setFlip("front");
    if (cardNumber + 1 === numberOfCards) {
      if (window.confirm(`Restart Cards For ${thisDeck.name}?`)) {
        setCardNumber(0);
      } else {
        history.push("/");
      }
    }
  };

  const handleFlip = () => {
    if (flip === "front") {
      setFlip("back");
    } else {
      setFlip("front");
    }
  };

  if (thisDeck.id) {
    if (numberOfCards > 2) {
      return (
        <section className="card">
          <div className="card-header">
            <Link to="/">Home</Link> /
            <Link to={`/decks/${thisDeck.id}`}> {thisDeck.name}</Link> / Study
          </div>
          <div className="card-body">
            <h1 className="card-title">Study: {thisDeck.name}</h1>
            <div className="card">{cardText}</div>
            <button
              type="button"
              onClick={handleFlip}
              className="btn btn-secondary m-1"
            >
              Flip
            </button>
            <button
              type="button"
              onClick={handleNext}
              className="btn btn-secondary m-1"
              hidden={flip === "front"}
            >
              Next
            </button>
          </div>
        </section>
      );
    }
    return (
      <section className="card">
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Study
        </div>
        <div className="card-body">
          <h1 className="card-title">Study: {thisDeck.name}</h1>
          <div className="card-text">
            {cardText}
            <p>
              You need at least 3 cards to study. There are {numberOfCards}{" "}
              cards in this deck.
            </p>
          </div>
          <Link
            type="button"
            to={`/decks/${thisDeck.id}/cards/new`}
            className="btn btn-primary"
          >
            + Add Card
          </Link>
        </div>
      </section>
    );
  }
  return <h1>Loading...</h1>;
}

export default Study;
