import React, { useEffect, useState } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck, updateCard, readCard } from "../../utils/api";

function DeskView() {
  const { deckId } = useParams();
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const history = useHistory();
  useEffect(() => {
    const abortController = new AbortController();
    async function fetchReadDeck() {
      try {
        console.log(deckId);
        const readDeckInfo = await readDeck(deckId, abortController.signal);
        setThisDeck(readDeckInfo);
      } catch (error) {
        setError(error);
        history.push(`/error/${error}`);
      }
    }
    fetchReadDeck(deckId);
  }, []);
  useEffect(() => {
    const thisDeckCards = thisDeck.cards;
    const listCards = thisDeckCards.map((card) => (
      <div className="row-2-col">
        <div className="col">{card.front}</div>
        <div className="col">{card.back}</div>
      </div>
    ));
  }, [thisDeck]);

  return (
    <section className="card">
      <div className="card-header">
        <Link to="/">Home</Link> / {thisDeck.name}
      </div>
      <div className="card-body">
        <div className="card-title">
          <h1>{thisDeck.name}</h1>
        </div>
        <div className="card-text">
          <p>{thisDeck.description}</p>
          <Link
            to={`/decks/${deckId}/edit`}
            type="button"
            className="btn btn-secondary m-1"
          >
            Edit
          </Link>
          <Link
            to={`/decks/${deckId}/study`}
            type="button"
            className="btn btn-primary m-1"
          >
            Study
          </Link>
        </div>
      </div>
    </section>
  );
}

export default DeskView;
