import React, { useEffect, useState, use } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck } from "../../utils/api";

function Card() {
  const { deckId, cardId } = useParams();
  const { url } = useRouteMatch();
  const [card, setCard] = useState({});
  const [breadCrumbs, setBreadCrumbs] = useState(<p>breadCrumbs</p>);
  const [cardType, setCardType] = useState(<p>card</p>);
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const [form, setForm] = useState(<p>form</p>);
  const history = useHistory();

  //form information
  const initialFormState = {
    front: "",
    email: "",
  };
  const [formData, setFormData] = useState({ ...initialFormState });
  const handleChange = ({ target }) => {
    setFormData({
      ...formData,
      [target.name]: target.value,
    });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    const abortController = new AbortController();
    createCard(deckId, formData, abortController.signal);
    setFormData({ ...initialFormState });
  };

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);

    if (!cardId) {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Add Card
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">{thisDeck.name}: Add Card</h1>
        </div>
      );
      setForm(
        <form onSubmit={handleSubmit}>
          <label>
            Front
            <br />
            <textarea
              type="text"
              id="front"
              name="front"
              value={formData.front}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Back
            <br />
            <textarea
              type="text"
              id="back"
              name="back"
              value={formData.back}
              onChange={handleChange}
            />
          </label>
          <br />
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-primary"
          >
            Save
          </Link>
          <button type="submit" className="btn btn-primary">
            Save
          </button>
        </form>
      );
    } else {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Edit Card {cardId}
        </div>
      );
      setCardType(
        <div>
          <h1 className="card-title">Edit Card</h1>
        </div>
      );
    }
  }, [thisDeck]);
  if (error) {
    history.push("/error");
  }

  if (!cardId) {
    return (
      <section className="card">
        {breadCrumbs} {cardType} {form}
      </section>
    );
  }
  return (
    <section className="card">
      {breadCrumbs} {cardType} {form}
    </section>
  );
}
export default Card;
