import React, { useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import CreateDeckButton from "./Card/CreateDeckButton";
import DeckList from "./Card/DeckList";
import Deck from "./Card/Deck";
import CreateDeck from "./Card/CreateDeck";
import DeckView from "./Card/DeckView";
import EditDeck from "./Card/EditDeck";
import { listDecks } from "../utils/api";
import Study from "./Card/Study";
import Card from "./Card/Card";
import {
  useParams,
  useHistory,
  Route,
  Switch,
  useRouteMatch,
} from "react-router-dom";

function Layout() {
  const [decks, setDecks] = useState([]);
  const [deckList, setDeckList] = useState([]);
  const [error, setError] = useState(undefined);
  const history = useHistory();
  const handleCreateDeck = () => {
    history.push("/decks/new");
  };

  useEffect(() => {
    const abortController = new AbortController();
    async function fetchListDecks() {
      try {
        const decksList = await listDecks(abortController.signal);

        if (decksList.length) {
          setDecks(decksList);

          // setDeckList(
          //   decksList.map((deck) => <Deck key={deck.id} deck={deck} />)
          // );
        }
      } catch (error) {
        setError(error);
        history.push(`/${error}`);
      }
    }
    fetchListDecks();
  }, []);

  return (
    <div>
      <Header />
      <div className="container">
        <Switch>
          <Route exact={true} path="/">
            <CreateDeckButton handleCreateDeck={handleCreateDeck} />
            <DeckList decks={decks} />
            {/* <main className="container">
              <section className="row">{deckList}</section>
            </main> */}
          </Route>
          <Route path="/decks/:deckId/study">
            <Study />
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <Card />
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <Card />
          </Route>
          <Route path="/decks/new">
            <CreateDeck />
          </Route>
          <Route path="/decks/:deckId/edit">
            <EditDeck />
          </Route>
          <Route path="/decks/:deckId">
            <DeckView />
          </Route>

          <Route>
            <NotFound />
          </Route>
        </Switch>
        {/* TODO: Implement the screen starting here */}
      </div>
    </div>
  );
}

export default Layout;
