import React, { useEffect, useState } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck, updateCard, readCard } from "../../utils/api";

function DeskView() {
  const { deckId } = useParams();
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const history = useHistory();
  useEffect(() => {
    const abortController = new AbortController();
    async function fetchReadDeck() {
      try {
        console.log(deckId);
        const readDeckInfo = await readDeck(deckId, abortController.signal);
        setThisDeck(readDeckInfo);
      } catch (error) {
        setError(error);
        history.push(`/error/${error}`);
      }
    }
    fetchReadDeck(deckId);
  }, []);

  return (
    <section className="card">
      <div className="card-header">
        <Link to="/">Home</Link> /
        <Link to="/decks/:deckId"> {thisDeck.name}</Link> / {cardTitle}
      </div>
      <div className="card-body">
        <div>
          <h1 className="card-title">
            {thisDeck.name}: {cardTitle}
          </h1>
        </div>
      </div>
    </section>
  );
}

export default DeskView;
