import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck, readDeck } from "../../utils/api";

function Deck({ deck, cards }) {
  const history = useHistory();
  const [error, setError] = useState(undefined);
  const [thisDeck, setThisDeck] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();
    async function fetchReadDeck(deck) {
      try {
        const readDeckInfo = await readDeck(deck.id, abortController.signal);
        setThisDeck(readDeckInfo);
      } catch (error) {
        setError(error);
        history.push("/error");
      }
    }
    fetchReadDeck(deck);
  }, []);

  const handleDeleteDeck = () => {
    const abortController = new AbortController();
    if (
      window.confirm(
        `Do you really want to delete deck ${deck.name}? There is no going back.`
      )
    ) {
      deleteDeck(deck.id, abortController.signal);
      console.log("deleted deck");
      history.push("/");
    }
  };

  if (thisDeck.id) {
    return (
      <article className="card container-fluid m-1">
        <div className="card-body">
          <h1 className="card-title">{deck.name}</h1>
          <p className="card-text">{deck.description}</p>
          <Link
            to={`/decks/${deck.id}`}
            type="button"
            className="btn btn-secondary m-1"
          >
            View
          </Link>
          <Link
            to={`/decks/${deck.id}/study`}
            type="button"
            className="btn btn-primary m-1"
          >
            Study
          </Link>
          <button
            onClick={handleDeleteDeck}
            type="button"
            className="btn btn-danger m-1"
          >
            Delete
          </button>
        </div>
        <div className="card-footer">{thisDeck.cards.length} Cards</div>
      </article>
    );
  }
  return <p>Loading...</p>;
}

export default Deck;
