import React, { useEffect, useState } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { createCard, readDeck, updateCard, readCard } from "../../utils/api";

function Card() {
  const { deckId, cardId } = useParams();
  //const { url } = useRouteMatch();
  const [card, setCard] = useState({});
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const history = useHistory();
  const initialFormState = {
    front: "",
    back: "",
    deckId: deckId,
  };
  const [formData, setFormData] = useState(initialFormState);
  const abortController = new AbortController();
  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(formData);
    const abortController = new AbortController();

    try {
      if (cardId) {
        await updateCard(formData, abortController.signal);
      } else {
        await createCard(deckId, formData, abortController.signal);
        setFormData(initialFormState);
      }
    } catch (error) {
      setError(error);
    }
  };
  const handleChange = ({ target }) => {
    setFormData({
      ...formData,
      [target.name]: target.value,
    });
  };

  const handleAddAnother = () => {
    setFormData(initialFormState);
  };

  useEffect(() => {
    async function fetchDeckInfo(deckId) {
      try {
        const deckInfo = await readDeck(deckId, abortController.signal);
        setThisDeck(deckInfo);
      } catch (error) {
        setError(error);
        history.push(`/error/${error}`);
      }
    }
    fetchDeckInfo(deckId);
  }, [deckId]);
  useEffect(() => {
    async function fetchCardInfo(cardId) {
      try {
        const card = await readCard(cardId, abortController.signal);
        setCard(card);
        setFormData({
          ...formData,
          id: card.id,
          front: card.front,
          back: card.back,
        });
      } catch (error) {
        setError(error);
      }
    }
    if (cardId) {
      fetchCardInfo(cardId);
    }
  }, [cardId]);
  const cardTitle = !cardId ? "Add Card" : `Edit Card ${cardId}`;
  return (
    <section className="card">
      <div className="card-header">
        <Link to="/">Home</Link> /
        <Link to="/decks/:deckId"> {thisDeck.name}</Link> / {cardTitle}
      </div>
      <div>
        <h1 className="card-title">
          {thisDeck.name}: {cardTitle}
        </h1>
      </div>
      <form onSubmit={handleSubmit} className="column">
        <label className="container-fluid">
          Front
          <br />
          <textarea
            type="text"
            id="front"
            name="front"
            value={formData.front}
            onChange={handleChange}
            className="w-100"
            placeholder="Front side of card"
          />
        </label>
        <br />
        <label className="container-fluid">
          Back
          <br />
          <textarea
            type="text"
            id="back"
            name="back"
            value={formData.back}
            onChange={handleChange}
            className="w-100"
            placeholder="Back side of card"
          />
        </label>
        <br />
        <Link
          to={`/decks/${deckId}`}
          type="button"
          className="btn btn-secondary m-1"
        >
          Done
        </Link>
        <button type="submit" className="btn btn-primary m-1">
          Save
        </button>
        {cardId && (
          <Link
            to={`/decks/${deckId}/cards/new`}
            className="btn btn-success m-1"
            type="button"
            onClick={handleAddAnother}
          >
            Add Another
          </Link>
        )}
      </form>
    </section>
  );
}
export default Card;
