import React, { useEffect, useState, use } from "react";
import { Link, useHistory, useRouteMatch, useParams } from "react-router-dom";
import { deleteDeck, readDeck } from "../../utils/api";

function Card() {
  const { deckId, cardId } = useParams();
  const { url } = useRouteMatch();
  const [breadCrumbs, setBreadCrumbs] = useState(<p>breadCrumbs</p>);
  const [card, setCard] = useState(<p>card</p>);
  const [thisDeck, setThisDeck] = useState([]);
  const [error, setError] = useState(undefined);
  const [form, setForm] = useState(<p>form</p>);

  useEffect(() => {
    const abortController = new AbortController();
    readDeck(deckId, abortController.signal).then(setThisDeck).catch(setError);

    if (!cardId) {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Add Card
        </div>
      );
      setCard(
        <div>
          <h1 className="card-title">{thisDeck.name}: Add Card</h1>
        </div>
      );
      setForm(
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      );
    } else {
      setBreadCrumbs(
        <div className="card-header">
          <Link to="/">Home</Link> /
          <Link to="/decks/:deckId"> {thisDeck.name}</Link> / Edit Card {cardId}
        </div>
      );
      setCard(
        <div>
          <h1 className="card-title">Edit Card</h1>
        </div>
      );
    }
  }, [thisDeck]);
  if (error) {
    history.push("/error");
  }

  if (!cardId) {
    return (
      <section className="card">
        {breadCrumbs} {card} {form}
      </section>
    );
  }
  return (
    <section className="card">
      {breadCrumbs} {card} {form}
    </section>
  );
}
export default Card;
